Data come in different types
.pull-left[ * Numeric (2 subtypes) - Integers (1, 50) - Double (1.5, 50.25, ?double) * Character ("hello") * Factor (grade = "A" | grade = "B") * Logical (TRUE | FALSE)

# Create some data
y &lt;- 6L # Integer
only_two_types_of_people &lt;- TRUE # Logical
me &lt;- "Paul"
grades = factor(c("A","B","C"))

]
.pull-right[

# What type are they
class(x)

[1] "numeric"

class(y)

[1] "integer"

class(only_two_types_of_people)

[1] "logical"

class(me)

[1] "character"

class(grades)

[1] "factor"


]
